import {
    GET_PRODUCTS,
    GET_DETAILS,
    GET_CATEGORIES,
    FILTER_BY_CATEGORIES

  } from "./types";

  const initialState = {
    allProducts: [],
    detailsProducts: {},
    categories : [],
    filterCategories : [],
    products:{}

  };

const rootReducer = (state = initialState, action) => {

    switch (action.type) {
case GET_PRODUCTS :
    return {
        ...state,
        allProducts: action.payload,
        filterCategories : action.payload,
        products : action.payload
    }

    case GET_DETAILS :
        return {
            ...state ,
            detailsProducts : action.payload
        }

        case GET_CATEGORIES :{
            return {
                ...state ,
                categories : action.payload
            }
        }
        case FILTER_BY_CATEGORIES:
          //hacemos una copia del estado
      const allProduct = [...state.filterCategories];
      const categorieFilter =
      //si el valor del target es all devolvemos todos los 
      action.payload === "all"
      ? state.products
      : allProduct.filter((el) => el.Categories.map(e=>e.name).includes(action.payload));


      return {
        ...state,
        allProducts: [...categorieFilter],
      };
    default:
        return state;
    }

};

export default rootReducer;
