import {
    GET_PRODUCTS,
    GET_DETAILS,
    GET_CATEGORIES

  } from "./types";

  const initialState = {
    allProducts: [],
    detailsProducts: {},
    categories : {},
    filterCategories : []

  };

const rootReducer = (state = initialState, action) => {

    switch (action.type) {
case GET_PRODUCTS :
    return {
        ...state,
        allProducts: action.payload,

    }

    case GET_DETAILS :
        return {
            ...state ,
            detailsProducts : action.payload
        }

        case GET_CATEGORIES :{
            return {
                ...state ,
                categories : action.payload
            }
        }
    default:
        return state;
    }

};

export default rootReducer;
