import {
    GET_PRODUCTS,

  } from "./types";

  const initialState = {
    allProducts: [],
    detailsProducts: []

  };

const rootReducer = (state = initialState, action) => {

    switch (action.type) {
case GET_PRODUCTS :
    return {
        ...state,
        allProducts: action.payload
    }
    default:
        return state;
    }

};

export default rootReducer;
