import axios from "axios";
import {
  GET_PRODUCTS,
  GET_DETAILS

} from "./types.js";

export const getAllProducts = () => {
    return async (dispatch) => {
      try {
        const allProducts = await axios.get(`http://localhost:3001/api/v1/products`);
        return dispatch({
          type:   GET_PRODUCTS,
          payload: allProducts.data,
        });
      } catch (error) {
        console.log(error);
      }
    };
  };


  //Get details for id
export const getFoodId = (id) => {
  return async (dispatch) => {
    try {
      const foodDetail = await axios(`http://localhost:3001/recipes/${id}`);
      return dispatch({
        type: GET_FOOD_DETAIL,
        payload: foodDetail.data,
      });
    } catch (error) {
      console.log(error);
    }
  };
};
