import {
    GET_PRODUCTS,
    GET_DETAILS,
    GET_CATEGORIES

  } from "./types";

  const initialState = {
    allProducts: [],
    detailsProducts: {},
    categories : {},
    filterCategories : []

  };

const rootReducer = (state = initialState, action) => {

    switch (action.type) {
case GET_PRODUCTS :
    return {
        ...state,
        allProducts: action.payload,
        filterCategories : action.payload
    }

    case GET_DETAILS :
        return {
            ...state ,
            detailsProducts : action.payload
        }

        case GET_CATEGORIES :{
            return {
                ...state ,
                categories : action.payload
            }
        }
        case FILTER_BY_DIEST:
      const allRecipes = [...state.filterRecipes];
      const dietsFilter =
        action.payload === "all"
          ? state.recipes
          : allRecipes.filter((el) => el.diets.includes(action.payload));
      return {
        ...state,
        allFoods: [...dietsFilter],
      };
    default:
        return state;
    }

};

export default rootReducer;
